% ME C176/BioE C119, Fall 2015% Asymmetrical Bending Analysis%************ YOU MAY NEED TO EDIT THIS SECTION ***************%			     WHEN ADDING THE STEEL IMPLANTSclear all;A=imread('femur','png');A = rgb2gray(A);  % Convert the image to grayscaleA = double(A);    % Convert the uint8 (8-bit) to a double                  %   This makes operations with A have double precisionres=0.78e-3;	% resolution in meters per pixel% Find BMD weighted centroid [You'll need to modify this for implants]sumy=0;sumz=0;total_sum=0;for i=1:86	for j=1:86            z=(j-1)*res+res/2;	% z position of pixel CENTER in m            y=(i-1)*res+res/2;	% y position of pixel center in m            sumz=sumz+z*A(i,j);            sumy=sumy+y*A(i,j);            total_sum = total_sum + A(i,j);	endend% Show imagefigure(1)imagesc(A),colormap(gray); hold on;xlabel('z (pixels)'); ylabel('y (pixels)'); axis square;% Centroid coodinatesy_hat=sumy/total_sum;	% in mz_hat=sumz/total_sum;	% in mfprintf('\n y_hat = %3.2f mm'  ,y_hat*1000);fprintf('\n z_hat = %3.2f mm\n',z_hat*1000);% Plot centroid axes on the image line([z_hat/res,z_hat/res],[1,86])line([1,86],[y_hat/res,y_hat/res])%***************ADD THE REST OF THE CODE HERE*******************